<?phpinclude('./handle.php');$h = new db_handle();if($h->is_logged_in() === false) die('You are not logged in');if(!isset($_GET['id'])) die('Bad input');$m = $h->get_message($_GET['id']);if($m === false) die('ID not found');echo '<table border=\'1\'>';echo '<tr><td><b>Sender</b></td><td>'.$h->id_to_user($m[3]);if(!$h->contact_exists($m[3])) echo ' <a href=\'add_contact.php?id='.strval($m[3]).'&r='.$m[0].'\'>(Add contact)</a>';echo '</td></tr>';echo '<tr><td><b>Time</b></td><td>'.date("j/n g:i A",$m[1]).'</td></tr>';echo '<tr><td><b>Subject</b></td><td>'.$m[4].'</td></tr>';echo '<tr><td><b>Body</b></td><td>'.$m[5].'</td></tr>';if(!empty($m[6])) echo '<tr><td><b>Attachment</b></td><td><a href=\'./attachments/'.$m[6].'\'>'.$m[6].'</a></td></tr>';echo '</table>';echo '<a href=\'./\'>Back</a> | <a href=\'send.php?r='.$_GET['id'].'\'>Reply</a>';?>